  upd_dt = now()
  
  , prd_cd = #{prd_cd}
  
  
  , brand_nm = #{brand_nm}
  
  
  , prd_nm = #{prd_nm}
  
  
  , jan_cd = #{jan_cd}
  
  
  , now_prd_cnt = #{now_prd_cnt}
  
where
  
  seq_id = #{seq_id}
  
  
  jan_cd = #{jan_cd}
