mapper.select:
select seq_id , prd_cd , brand_nm , prd_nm , jan_cd , now_prd_cnt , prd_qty , prd_case , prd_bara , prd_lot , dealer_id , dealer_nm , sell_prc , date_format(exp_dt, '%Y/%m/%d') exp_dt , date_format(reg_dt, '%Y/%m/%d') reg_dt , date_format(upd_dt, '%Y/%m/%d') upd_dt from jaiko_inventory_info
seq_id = #{seq_id}
and prd_cd like '%${prd_cd}%'
and prd_nm like '%${prd_nm}%'
and jan_cd like '%${jan_cd}%'
and dealer_id = #{dealer_id}
and seq_id in
#{item}
upd_dt between date_add(now(), interval -1 day) and now()
order by upd_dt desc, seq_id desc

mapper.insert:
insert into jaiko_inventory_info (prd_cd, brand_nm, prd_nm, jan_cd, now_prd_cnt
, prd_qty, prd_case, prd_bara, exp_dt, sell_prc
, prd_lot, dealer_id, dealer_nm)
values (#{prd_cd}, #{brand_nm}, #{prd_nm}, #{jan_cd}, #{now_prd_cnt}
, #{prd_qty}, #{prd_case}, #{prd_bara}, STR_TO_DATE('9999/12/31','%Y/%m/%d'), #{sell_prc}
, #{prd_lot}, #{dealer_id}, #{dealer_nm})

mapper.update:
update jaiko_inventory_info
set
  upd_dt = now()
  
  , prd_cd = #{prd_cd}
  
  
  , brand_nm = #{brand_nm}
  
  
  , prd_nm = #{prd_nm}
  
  
  , jan_cd = #{jan_cd}
  
  
  , now_prd_cnt = #{now_prd_cnt}
  
  
  , prd_qty = #{prd_qty}
  
  
  , prd_case = #{prd_case}
  
  
  , prd_bara = #{prd_bara}
  
  
  , exp_dt = STR_TO_DATE(#{exp_dt},'%Y/%m/%d')
  
  
  , sell_prc = #{sell_prc}
  
  
  , prd_lot = #{prd_lot}
  
  
  , dealer_id = #{dealer_id}
  
  
  , dealer_nm = #{dealer_nm}
  
where
  
  seq_id = #{seq_id}
  
  
  jan_cd = #{jan_cd}
