mapper.insert:
insert into jaiko_prd_info (prd_cd, brand_nm, prd_nm, jan_cd, prd_cnt
, prd_unit_prc, tax_incld, tax_rt, std_info
, partner_id, partner_nm, dsku, dasin)
values (#{prd_cd}, #{brand_nm}, #{prd_nm}, #{jan_cd}, #{prd_cnt}
, #{prd_unit_prc}, #{tax_incld}, #{tax_rt}, #{std_info}
, #{partner_id}, #{partner_nm}, #{dsku}, #{dasin})

mapper.update:
update jaiko_prd_info
set
  upd_dt = now()
  
  , partner_id = #{partner_id}
  
  
  , partner_nm = #{partner_nm}
  
  
  , prd_cd = #{prd_cd}
  
  
  , brand_nm = #{brand_nm}
  
  
  , prd_nm = #{prd_nm}
  
  
  , jan_cd = #{jan_cd}
  
  
  , prd_cnt = #{prd_cnt}
  
  
  , prd_unit_prc = #{prd_unit_prc}
  
  
  , dsku = #{dsku}
  
  
  , dasin = #{dasin}
  
  
  , tax_incld = #{tax_incld}
  
  
  , tax_rt = #{tax_rt}
  
  
  , std_info = #{std_info}
  
where
  
  seq_id = #{seq_id}
  
  
  jan_cd = #{jan_cd}
