mapper.insert:
insert into jaiko_sales_info (partner_id, partner_nm, gbn


, mid_tot, memo, dlv_dt, bill_dt


, prd_cd, prd_nm, jan_cd, dlv_prc
, tax_rt, cnsp_tax, dlv_dt


)
values (#{partner_id}, #{partner_nm}, #{gbn}


, #{mid_tot}, #{memo}, str_to_date(#{dlv_dt}, '%Y-%m-%d')
, str_to_date(#{bill_dt}, '%Y-%m-%d'))


, #{prd_cd}, #{prd_nm}, #{jan_cd}, #{dlv_prc}
, #{tax_rt}, #{cnsp_tax}, str_to_date(#{dlv_dt}, '%Y-%m-%d'))

mapper.select:
select seq_id, partner_id, partner_nm, gbn, prd_nm, prd_cd, jan_cd ,entry_no, qty, dlv_prc, tax_rt, tot, cnsp_tax, sub_tot ,sales_no, spec, mid_tot, bill_no, pay_method, pay_comp_yn ,manager, mail, memo ,date_format(dlv_dt, '%Y-%m-%d') dlv_dt ,date_format(bill_dt, '%Y-%m-%d') bill_dt ,date_format(reg_dt, '%Y-%m-%d') reg_dt ,date_format(upd_dt, '%Y-%m-%d') upd_dt from jaiko_sales_info
partner_id = #{partner_id}
and dlv_dt between str_to_date(#{from_dt}, '%Y%m%d%H%i%s') and str_to_date(#{to_dt}, '%Y%m%d%H%i%s')
and gbn = #{gbn}
and gbn != 'TOT'

mapper.update:
update jaiko_sales_info
set
	upd_dt = now()

	, sales_no = #{sales_no}


	, prd_nm = #{prd_nm}


	, prd_cd = #{prd_cd}


	, jan_cd = #{jan_cd}


	, spec = #{spec}


	, entry_no = #{entry_no}


	, qty = #{qty}


	, dlv_prc = #{dlv_prc}


	, tax_rt = #{tax_rt}


	, tot = #{tot}


	, cnsp_tax = #{cnsp_tax}


	, sub_tot = #{sub_tot}


	, mid_tot = #{mid_tot}


	, bill_no = #{bill_no}


	, pay_method = #{pay_method}


	, pay_comp_yn = #{pay_comp_yn}


	, manager = #{manager}


	, mail = #{mail}


	, memo = #{memo}


	, bill_dt = str_to_date(#{bill_dt}, '%Y-%m-%d')

where
	seq_id = #{seq_id}
