mapper.select:
select seq_id , partner_id , partner_nm , prd_cd , brand_nm , prd_nm , jan_cd , prd_cnt , prd_unit_prc , dsku , dasin , tax_incld , tax_rt , date_format(warehouse_dt, '%Y/%m/%d %H:%i:%s') warehouse_dt , date_format(delivery_dt, '%Y/%m/%d %H:%i:%s') delivery_dt , date_format(reg_dt, '%Y/%m/%d') reg_dt , date_format(upd_dt, '%Y/%m/%d') upd_dt from jaiko_warehouse_info where
jan_cd = #{jan_cd}
warehouse_dt between date_format (#{from_dt}, '%Y%m%d%H%i%s') and date_format (#{to_dt}, '%Y%m%d%H%i%s')

mapper.insert:
insert into jaiko_warehouse_info ( partner_id, partner_nm, prd_cd, brand_nm, prd_nm , jan_cd, prd_cnt, prd_unit_prc , dsku, dasin, tax_incld, tax_rt
, warehouse_dt
, delivery_dt
) select partner_id, partner_nm, prd_cd, brand_nm, prd_nm , jan_cd, prd_cnt, prd_unit_prc , dsku, dasin, tax_incld, tax_rt
, now()
, now()
from jaiko_prd_info where
jan_cd = #{jan_cd}
seq_id = #{seq_id}

mapper.update:
update jaiko_warehouse_info set upd_dt = now()
, warehouse_dt = now()
, delivery_dt = now()
where
jan_cd = #{jan_cd}
seq_id = #{seq_id}
