mapper.update:
update 
	translation_info
set
	before_trans = #{before_trans}
	, after_trans = #{after_trans}
	, update_date = now()
where
	seq_id = #{seq_id}

update
	rakuten_info
set
	
	baggage_claim_no = #{baggage_claim_no}
	
	
	delivery_company = #{delivery_company}
	
	, update_date = now()
where
	seq_id = #{seq_id}

mapper.select:
select tr.seq_id , date_format(tr.register_date, '%Y/%m/%d') register_date , err_text , result_text , order_no , product_name , product_option , unit_no , delivery_surname , delivery_name , delivery_company from translation_result tr inner join rakuten_info ri on ri.seq_id = tr.trans_target_id and tr.trans_target_type = 'R' left outer join translation_err terr on terr.trans_target_id = ri.seq_id and terr.trans_target_type = 'R'
tr.trans_target_id = #{trans_target_id}
tr.seq_id in
#{item}

mapper.update:
update 
	translation_result
set
	result_text = #{result_text}
	, register_date = now()

	 
		seq_id = #{seq_id}
	
	
		trans_target_id = #{trans_target_id}

