mapper.select:
select tr.seq_id , date_format(qi.register_date, '%Y/%m/%d') register_date , date_format(qi.update_date, '%Y/%m/%d') update_date , err_text , result_text , delivery_sts , order_no , cart_no , delivery_company_q10 , invoice_no , ship_date , order_date , pay_date , delivery_date , ship_eta , delivery_atd , ship_method , item_no , product_name , qty , option_info , option_cd , bonus , recpt_name , recpt_name_kana , recpt_phone_no , recpt_mobile_no , address , post_no , country_name , ship_pay , pay_site , currency_type , pay_amt , sel_price , disc_price , total_price , total_supply , pay_name , pay_name_kana , delivery_item , pay_phone_no , pay_mobile_no , product_cd , jan_cd , std_no , presenter , ads_no_site , material_type , delivery_company from translation_result tr inner join q10_info qi on qi.seq_id = tr.trans_target_id and tr.trans_target_type = 'Q' left outer join translation_err terr on terr.trans_target_id = qi.seq_id and terr.trans_target_type = 'Q'
tr.trans_target_id = #{trans_target_id}
tr.seq_id in
#{item}
and delivery_company in
#{item}
and delivery_company = #{delivery_company}
order by pay_date desc

mapper.update:
update
	q10_info
set
	update_date = now()
	
	, delivery_company = #{delivery_company}
	
	
	, invoice_no = #{invoice_no}
	
where
	seq_id = #{seq_id}
