mapper.select:
select tr.seq_id , ri.seq_id real_seq_id , err_text , date_format(ri.register_date, '%Y/%m/%d') register_date , date_format(ri.update_date, '%Y/%m/%d') update_date , result_text , delivery_company , order_no , order_status , sub_status_id , sub_status , order_datetime , order_date , order_time , cancel_due_date , order_check_datetime , order_confirm_datetime , delivery_eta_datetime , delivery_ata_datetime , pay_method_name , creadit_pay_method , credit_pay_times , delivery_method , delivery_type , order_type , multi_destination_flag , destination_match_flag , island_flag , rverify_flag , warning_type , rmember_flag , purchase_hist_mod_flag , total_goods_amt , total_consume_tax , total_shipping , gross_deduction , invoice_amt , total_amt , point_usage , total_coupon_usage , store_coupon_usage , rakuten_coupon_usage , order_post_no1 , order_post_no2 , order_add1 , order_add2 , order_add3 , order_surname , order_name , order_surname_kana , order_name_kana , order_tel1 , order_tel2 , order_tel3 , order_email , order_sex , request_no , request_received_no , ship_id , ship_charge , ship_substitute_fee , ship_total_consume_tax , ship_total_goods_amt , ship_total_amt , indicates , delivery_post_no1 , delivery_post_no2 , delivery_add1 , delivery_add2 , delivery_add3 , delivery_surname , delivery_name , delivery_surname_kana , delivery_name_kana , delivery_tel1 , delivery_tel2 , delivery_tel3 , product_detail_id , product_id , product_name , product_no , product_manage_no , unit_price , unit_no , delivery_cost_include , tax_exclude , substitute_fee_include , product_option , point_multiple , delivery_info , inventory_type , wrap_title1 , wrap_name1 , wrap_amt1 , wrap_tax_include1 , wrap_type1 , wrap_title2 , wrap_name2 , wrap_amt2 , wrap_tax_include2 , wrap_type2 , delivery_time , delivery_date_sel , manager , quick_note , msg_to_customer , gift_request , comment , util_terminal , mail_carrier_code , tomorrow_hope , drug_order_flag , rakuten_super_deal , membership_program from translation_result tr inner join rakuten_info ri on ri.seq_id = tr.trans_target_id and tr.trans_target_type = 'R' left outer join translation_err terr on terr.trans_target_id = ri.seq_id and terr.trans_target_type = 'R'
tr.trans_target_id = #{trans_target_id}
tr.seq_id in
#{item}
and delivery_company = #{delivery_company}
UNION ALL select tr.seq_id , rfi.seq_id real_seq_id , err_text , date_format(rfi.register_date, '%Y/%m/%d') register_date , date_format(rfi.update_date, '%Y/%m/%d') update_date , result_text , delivery_company , order_no , order_status , sub_status_id , sub_status , order_datetime , order_date , order_time , cancel_due_date , order_check_datetime , order_confirm_datetime , delivery_eta_datetime , delivery_ata_datetime , pay_method_name , creadit_pay_method , credit_pay_times , delivery_method , delivery_type , order_type , multi_destination_flag , destination_match_flag , island_flag , rverify_flag , warning_type , rmember_flag , purchase_hist_mod_flag , total_goods_amt , total_consume_tax , total_shipping , gross_deduction , invoice_amt , total_amt , point_usage , total_coupon_usage , store_coupon_usage , rakuten_coupon_usage , order_post_no1 , order_post_no2 , order_add1 , order_add2 , order_add3 , order_surname , order_name , order_surname_kana , order_name_kana , order_tel1 , order_tel2 , order_tel3 , order_email , order_sex , request_no , request_received_no , ship_id , ship_charge , ship_substitute_fee , ship_total_consume_tax , ship_total_goods_amt , ship_total_amt , indicates , delivery_post_no1 , delivery_post_no2 , delivery_add1 , delivery_add2 , delivery_add3 , delivery_surname , delivery_name , delivery_surname_kana , delivery_name_kana , delivery_tel1 , delivery_tel2 , delivery_tel3 , product_detail_id , product_id , product_name , product_no , product_manage_no , unit_price , unit_no , delivery_cost_include , tax_exclude , substitute_fee_include , product_option , point_multiple , delivery_info , inventory_type , wrap_title1 , wrap_name1 , wrap_amt1 , wrap_tax_include1 , wrap_type1 , wrap_title2 , wrap_name2 , wrap_amt2 , wrap_tax_include2 , wrap_type2 , delivery_time , delivery_date_sel , manager , quick_note , msg_to_customer , gift_request , comment , util_terminal , mail_carrier_code , tomorrow_hope , drug_order_flag , rakuten_super_deal , membership_program from translation_result tr inner join rakuten_frozen_info rfi on rfi.seq_id = tr.trans_target_id and tr.trans_target_type = 'RF' left outer join translation_err terr on terr.trans_target_id = rfi.seq_id and terr.trans_target_type = 'RF'
tr.trans_target_id = #{trans_target_id}
tr.seq_id in
#{item}
and delivery_company = #{delivery_company}
order by order_datetime desc

mapper.update:
update
	rakuten_info
set
	update_date = now()
	
	, baggage_claim_no = #{baggage_claim_no}
	
	
	, delivery_company = #{delivery_company}
	
	
	, product_name = #{product_name}
	
where
	seq_id = #{seq_id}

update
	rakuten_frozen_info
set
	update_date = now()
	
	, baggage_claim_no = #{baggage_claim_no}
	
	
	, delivery_company = #{delivery_company}
	
	
	, product_name = #{product_name}
	
where
	seq_id = #{seq_id}

mapper.select:
select seq_id , order_no , order_status , baggage_claim_no , delivery_surname , delivery_name , delivery_surname_kana , delivery_name_kana , delivery_tel1 , delivery_tel2 , delivery_tel3 , delivery_add1 , delivery_add2 , delivery_add3 , delivery_company , '' free_space , ship_id , '' ship_detail_id , date_format(register_date, '%Y%m%d') ship_date from rakuten_info
seq_id in
#{item}
and delivery_company = #{delivery_company}

mapper.delete:
delete from rakuten_frozen_info

	
	seq_id = #{seq_id}
	
	
	order_no like '%${order_no}%'
