mapper.update:
update
	prd_info
set
	update_date = now()
	
	, prd_cd = #{prd_cd}
	
	
	, jan_cd = #{jan_cd}
	
	
	, prd_nm = #{prd_nm}
	
	
	, prd_dtl = #{prd_dtl}
	
	
	, prd_cnt = #{prd_cnt}
	
	
	, order_no = #{order_no}
	
where
	
		
		jan_cd = #{jan_cd}
		
		
		seq_id = #{seq_id}

update
	dealer_info
set
	update_date = now()
	
	, dealer_id = #{dealer_id}
	
	
	, dealer_nm = #{dealer_nm}
	
where
	seq_id = #{seq_id}

mapper.select:
select sti.seq_id, jan_cd, prd_nm, prd_cnt, di.dealer_id, di.dealer_nm , date_format(receive_date, '%Y/%m/%d') receive_date from stock_info sti inner join dealer_info di on di.dealer_id = sti.dealer_id
sti.seq_id = #{seq_id}
and jan_cd = #{jan_cd}
and receive_date between str_to_date(concat(#{receive_date},' ','00:00:00'), '%Y/%m/%d %H:%i:%S') and str_to_date(concat(#{receive_date},' ','23:59:59'), '%Y/%m/%d %H:%i:%S')
and (di.dealer_id like '%${dealer_id}%' or di.dealer_nm like '%${dealer_id}%')
